        jQuery(window).ready(function ($) {
            if ($(window).width() > 1024) var w = $(window).width() ;
            else var w = $(window).width();
            $('.slant-L').css('border-left-width', w);
            $('.slant-R').css('border-right-width', w);
            $(window).on('smartresize', function () {
                var w = $(window).width();
                if (w < 768)

                    var ipadw = w
                if (w > 768)

                    var ipadw = w + 700
                $('.slant-L').css('border-left-width', w);
                $('.slant-R').css('border-right-width', w);
            });
        });
